import React from "react";
import { useState } from "react";
import { useNavigate } from "react-router-dom";
import useStateRef from 'react-usestateref';

const CreateExamMaster = () => {
  const navigate=useNavigate();
  const [hasError, setHasError,hasNoError] = useStateRef(false);
  const [examid, setexamid] = useState("");
  const [examname, setexamname] = useState("");
  const [description, setdescription] = useState("");
  const [creationdate, setcreationdate] = useState("");
  const [expirationdate, setexpirationdate] = useState("");
  const [noofquestions, setnoofquestions] = useState("");
  const [durationminutes, setdurationminutes] = useState("");
  const [passpercentage, setpasspercentage] = useState("");
  const [questionsrandomized, setquestionsrandomized] = useState("");
  const [answersmust, setanswersmust] = useState("");
  const [enablenegativemark, setenablenegativemark] = useState("");
  const [negativemarkvalue, setnegativemarkvalue] = useState("");
  const [examList,setExamList]=useState([]);
  // let examlist=localStorage.getItem("examDetails");
  // if(examlist!=null){
  //   examlist=localStorage.getItem("examDetails");
  //   setExamList([...examlist]);
  // }
  // else{
  //   localStorage.clear();
  // }
  // console.log("examlist.......",examlist);
   let examlist=localStorage.getItem('examDetails');
   examlist.forEach((element,index,array)=>{
    console.log("examId...",element.examId);
   })
  // examlist.entries();
  // for(let key in examlist){
  //   console.log( key + "key....>",examlist[key]);
  // }

  const myobject = Object.fromEntries(examlist);
  Object.entries(myobject).map(([key, value], keyIndex) => {
    console.log("key",key);
    console.log("value...",value);
  });
  // if(examlist !==null){
  //   examlist=localStorage.getItem('examDetails');
  //   // localStorage.clear();
  //   console.log("This is the examList from the show exams...",examlist);
  // }
  // else{
   
  //   console.log("this is the else condition...",examlist);
  // }
  const values=JSON.stringify(examlist);
  console.log("values...",values);
  console.log("------------->",values.toString());

  let map={
    values : values,
  }
  console.log("my map...",map);
  console.log(examlist);
  console.log(examList);
  const submittingForm=(e)=>{
    console.log("Form submittted....");
    e.preventDefault();
    const data = new FormData(e.target);
    setHasError(false);
    handleErrors();

    const myobject = Object.fromEntries(data.entries());
    Object.entries(myobject).map(([key, value], keyIndex) => {
      validateExamMaster(key, value);
    });


  }

  const handleSubmit = async (e) => {
    e.preventDefault();

    const formElement=document.getElementById('myform');
    const data = new FormData(formElement);
    handleErrors();

    const examId = data.get("examId");
    console.log("examId-----", examId);
    const examName = data.get("examName");
    const description = data.get("description");
    const creationDate = data.get("creationDate");
    const expirationDate = data.get("expirationDate");
    const noOfQuestions = data.get("noOfQuestions");
    const durationMinutes = data.get("durationMinutes");
    const passPercentage = data.get("passPercentage");
    const questionsRandomized = data.get("questionsRandomized");
    const answersMust = data.get("answersMust");
    const enableNegativeMark = data.get("enableNegativeMark");
    const negativeMarkValue = data.get("negativeMarkValue");

   
    // alert("form submmitted successfully")
    // document.getElementById('btn1').classList.remove('d-block');
    // document.getElementById('btn1').classList.add('d-none');
    // document.getElementById('btn2').classList.remove('d-none');
    // document.getElementById('btn2').classList.add('d-block');
    // document.getElementById('btn3').classList.remove('d-none');
    // document.getElementById('btn3').classList.add('d-block');

    const myobject = Object.fromEntries(data.entries());
    Object.entries(myobject).map(([key, value], keyIndex) => {
      validateformexammaster(key, value);
  });


    function validateformexammaster(key,value){

      switch(key){
        case "examId" : {

          if(value=="" || value==null){
              // alert("examId should not be em")
          }
        }
      }

    }

    const map = {
      examId: examId,
      examName: examName,
      description: description,
      creationDate: creationDate,
      expirationDate: expirationDate,
      noOfQuestions: noOfQuestions,
      durationMinutes: durationMinutes,
      passPercentage: passPercentage,
      questionsRandomized: questionsRandomized,
      answersMust: answersMust,
      enableNegativeMark: enableNegativeMark,
      negativeMarkValue: negativeMarkValue,
    };
    console.log(map);
    if(!hasNoError.current){
      fetch("https://localhost:8443/onlineexam/control/ExamMasterEvent", {
        method: "POST",
        body: JSON.stringify(map),
        headers: {
          "Content-Type": "application/json",
          Accept: "application/json",
        },
      })
        .then((response) => alert("Value added succesfully"))
        .catch((err) => console.log("ERROR FROM FETCH", err));
  
    }
   
 };



  function handleErrors(){
        document.getElementById('p1').classList.add('d-none');
        document.getElementById('p2').classList.add('d-none');
        document.getElementById('p2').classList.innerHTML='';
        document.getElementById('p3').classList.add('d-none');
        document.getElementById('p3').classList.innerHTML='';
        document.getElementById('p4').classList.add('d-none');
        document.getElementById('p4').classList.innerHTML='';
        document.getElementById('p5').classList.add('d-none');
        document.getElementById('p5').classList.innerHTML='';
        document.getElementById('p6').classList.add('d-none');
        document.getElementById('p6').classList.innerHTML='';
        document.getElementById('p7').classList.add('d-none');
        document.getElementById('p7').classList.innerHTML='';
        document.getElementById('p8').classList.add('d-none');
        document.getElementById('p8').classList.innerHTML='';
        document.getElementById('p9').classList.add('d-none');
        document.getElementById('p9').classList.innerHTML='';
        document.getElementById('p10').classList.add('d-none');
        document.getElementById('p10').classList.innerHTML='';
        document.getElementById('p11').classList.add('d-none');
        document.getElementById('p11').classList.innerHTML='';
        document.getElementById('p12').classList.add('d-none');
        document.getElementById('p12').classList.innerHTML='';
        setHasError(false);

  }

  const validateExamMaster=(key,value)=>{

    switch(key){

      case "examId":
        if(value === null || value === ""){
          document.getElementById("p1").classList.remove("d-none");
          document.getElementById("p1").classList.add("d-block");
          document.getElementById("p1").innerHTML = "Please enter a examId";
          setHasError(true);
        }
        break;
      case "examName":
        
          if(value == "" || value == null){
            document.getElementById("p2").classList.remove("d-none");
            document.getElementById("p2").classList.add("d-block");
            document.getElementById("p2").innerHTML = "Please enter a examName";
            setHasError(true);
          }
        break;

      case "description":

      if(value == "" || value == null){
        document.getElementById("p3").classList.remove("d-none");
        document.getElementById("p3").classList.add("d-block");
        document.getElementById("p3").innerHTML = "Please enter a description";
        setHasError(true);
      }

      case "creationDate":
        if(value == "" || value == null){
          document.getElementById("p4").classList.remove("d-none");
          document.getElementById("p4").classList.add("d-block");
          document.getElementById("p4").innerHTML = "Please enter a creation date";
          setHasError(true);
        }
      break;

      case "expirationDate":
        if(value == "" || value == null){
          document.getElementById("p5").classList.remove("d-none");
          document.getElementById("p5").classList.add("d-block");
          document.getElementById("p5").innerHTML = "Please enter a expiration date";
          setHasError(true);
        }
      break;

      case "noOfQuestions":
        if(value == "" || value == null){
          document.getElementById("p6").classList.remove("d-none");
          document.getElementById("p6").classList.add("d-block");
          document.getElementById("p6").innerHTML = "Please enter a noOfQuestions";
          setHasError(true);
        }
      break;
      
      case "durationMinutes":
        if(value == "" || value == null){
          document.getElementById("p7").classList.remove("d-none");
          document.getElementById("p7").classList.add("d-block");
          document.getElementById("p7").innerHTML = "Please enter a durationMinutes";
          setHasError(true);
        }
      break;

      
      case "passPercentage":
        if(value == "" || value == null){
          document.getElementById("p8").classList.remove("d-none");
          document.getElementById("p8").classList.add("d-block");
          document.getElementById("p8").innerHTML = "Please enter a passPercentage";
          setHasError(true);
        }
      break;

      
      case "questionsRandomized":
        if(value == "" || value == null){
          document.getElementById("p9").classList.remove("d-none");
          document.getElementById("p9").classList.add("d-block");
          document.getElementById("p9").innerHTML = "Please enter a questionsRandomized";
          setHasError(true);
        }
      break;

      
      case "answersMust":
        if(value == "" || value == null){
          document.getElementById("p10").classList.remove("d-none");
          document.getElementById("p10").classList.add("d-block");
          document.getElementById("p10").innerHTML = "Please enter a answersMust";
          setHasError(true);
        }
      break;

      case "enableNegativeMark":
        if(value == "" || value == null){
          document.getElementById("p11").classList.remove("d-none");
          document.getElementById("p11").classList.add("d-block");
          document.getElementById("p11").innerHTML = "Please enter a enableNegativeMark";
          setHasError(true);
        }
      break;

      case "negativeMarkValue":
        if(value == "" || value == null){
          document.getElementById("p12").classList.remove("d-none");
          document.getElementById("p12").classList.add("d-block");
          document.getElementById("p12").innerHTML = "Please enter a negativeMarkValue";
          setHasError(true);
        }
      break;

    
        
      default:{
        console.log("FOrm validated successfully...");
      }

    }
  }

  return (
    <div className="container my-3">
      <legend className="rounded-pill bg-primary text-white">Create Exam</legend>
      <div className="col-10 mx-auto">
        <form className="d-flex" id="myform" onSubmit={handleSubmit}>
          <div className="col-5">
            <div className="mb-3">
              <label htmlFor="exampleInputEmail1" className="form-label float-start">
                ExamId*:
              </label>
              <input
                type="text"
                id="examid"
                className="form-control "
                placeholder="ExamId * eg(1)"
                name="examId"
                value={examid}
                onChange={(e) => setexamid(e.target.value)}
              />
              <p id="p1" className="text-danger"></p>
            </div>
            <div className="mb-3">
              <label htmlFor="exampleInputEmail1" className="form-label float-start">
                Exam Name:
              </label>
              <input
                type="text"
                id="examname"
                className="form-control"
                placeholder="Examname"
                name="examName"
                value={examname}
                onChange={(e) => setexamname(e.target.value)}
              />
              <p id="p2" className="text-danger"></p>
            </div>
            <div className="mb-3">
              <label htmlFor="exampleInputEmail1" className="form-label float-start">
                Description:
              </label>
              <input
                type="text"
                id="description"
                className="form-control"
                placeholder="Description"
                name="description"
                value={description}
                onChange={(e) => setdescription(e.target.value)}
              />
              <p id="p3" className="text-danger"></p>
            </div>
            <div className="mb-3">
              <label htmlFor="" className="form-label float-start">
                Creationdate:
              </label>
              <input
                type="datetime-local"
                id="creationdate"
                className="form-control"
                name="creationDate"
                value={creationdate}
                onChange={(e) => setcreationdate(e.target.value)}
              />
              <p id="p4" className="text-danger"></p>
            </div>
            <div className="mb-3">
              <label htmlFor="exampleInputEmail1" className="form-label float-start">
                Expiration Date:
              </label>
              <input
                type="date"
                id="expirationdate"
                className="form-control"
                name="expirationDate"
                value={expirationdate}
                onChange={(e) => setexpirationdate(e.target.value)}
              />
              <p id="p5" className="text-danger"></p>
            </div>
            <div className="mb-3">
              <label htmlFor="exampleInputPassword1" className="form-label float-start">
                NoOfQuestions:
              </label>
              <input
                type="text"
                id="noofquestions"
                className="form-control"
                placeholder="No of questions"
                name="noOfQuestions"
                value={noofquestions}
                onChange={(e) => setnoofquestions(e.target.value)}
              />
              <p id="p6" className="text-danger"></p>
            </div>
            <div>
            <button type="submit" className="btn btn-primary d-block" >
          Back
        </button>
            </div>
          </div>

          <div className="col-5 offset-1">
            <div className="mb-3">
              <label htmlFor="exampleInputEmail1" className="form-label float-start">
                Duration Minutes:
              </label>
              <input
                type="text"
                id="durationminutes"
                placeholder="Duration Minutes eg(60-Minutes)"
                name="durationMinutes"
                className="form-control"
                value={durationminutes}
                onChange={(e) => setdurationminutes(e.target.value)}
              />
              <p id="p7" className="text-danger"></p>
            </div>
            <div className="mb-3">
              <label htmlFor="exampleInputEmail1" className="form-label float-start">
              Passpercentage:
              </label>
              <input
                type="text"
                id="passpercentage"
                placeholder="pass-percentage eg(25%)"
                name="passPercentage"
                className="form-control"
                value={passpercentage}
                onChange={(e) => setpasspercentage(e.target.value)}
              />
              <p id="p8" className="text-danger"></p>
            </div>
            <div className="mb-3">
              <label htmlFor="exampleInputEmail1" className="form-label float-start">
              QuestionsRandomized:
              </label>
              <input
                type="text"
                name="questionsRandomized"
                className="form-control"
                id="questionsrandomized"
                placeholder="questions randomized eg(Yes or No)"
                value={questionsrandomized}
                onChange={(e) => setquestionsrandomized(e.target.value)}
              />
              <p id="p9" className="text-danger"></p>
            </div>
            <div className="mb-3">
              <label htmlFor="exampleInputEmail1" className="form-label float-start">
                Answers Must:
              </label>
              <input
                type="text"
                name="answersMust"
                placeholder="To Answer is must eg(Y OR N)"
                id="answersmust"
                className="form-control"
                value={answersmust}
                onChange={(e) => setanswersmust(e.target.value)}
              />
              <p id="p10" className="text-danger"></p>
            </div>
            <div className="mb-3">
              <label htmlFor="exampleInputEmail1" className="form-label float-start">
              EnableNegativeMark:
              </label>
              <input
                type="text"
                name="enableNegativeMark"
                placeholder="Enablenegativemark eg(Y OR N)"
                id="enablenegativemark"
                className="form-control"
                value={enablenegativemark}
                onChange={(e) => setenablenegativemark(e.target.value)}
              />
              <p id="p11" className="text-danger"></p>
            </div>
            <div className="mb-3">
              <label htmlFor="exampleInputEmail1" className="form-label float-start">
              NegativeMarkValue:
              </label>
              <input
                type="text"
                placeholder="Negativemarkvalue eg(0.25)"
                id="negativemarkvalue"
                name="negativeMarkValue"
                className="form-control"
                value={negativemarkvalue}
                onChange={(e) => setnegativemarkvalue(e.target.value)}
              />
              <p id="p12" className="text-danger"></p>
            </div>
            
            <div>
              <button className="btn btn-primary">Add Another Exam</button>
           {/* <button className="btn btn-primary "  onClick={()=>{handleSubmit}}>Next</button>  */}
          </div>
          </div>
        </form>
        
      </div>
    </div>
  );
};

export default CreateExamMaster;
