import React, { useEffect } from 'react'
import { useParams } from 'react-router-dom';
import { useState } from 'react';

const DetailsOfQuestion = () => {
    const [data,setData]=useState([]);
    const params=useParams();
    const questionId = params.questionId;
    const topicName=params.topicName;
    console.log("Topic Name....>",topicName);
    console.log("Question Id...>",questionId);
    async function questionDetails()  {
        console.log("question Details is called....");
        console.log(questionId);
        const map = {
          questionId: questionId,
        };
    
        const response = await fetch(
          "https://localhost:8443/onlineexam/control/questionMasterList",
          {
            method: "POST",
            credentials: "include",
            body: JSON.stringify(map),
            headers: {
              "Content-type": "application/json",
            },
          }
        );
    
        const result = await response.json();
        console.log("result....>",result);
        const myobj = result.getRecord;
        console.log("Myobj....>",myobj);
        setData(result.getRecord);
        console.log("Data..>",data);
        console.log(result.getRecord);
        // const topicname = result.resultMap.topicName;
        // let resultvalue = {
        //   myobj: myobj,
        //   topicname: topicname,
        // };
        // console.log("result", result.resultMap.listQuestions);
        // console.log("second result,", result.resultMap.topicName);
        // setQuestionData([...result.resultMap.listQuestions]);
        // // setQuestionData([ ...resultvalue]);
        // console.log("result value......", resultvalue);
        // console.log("question data....", questionData);
        // setTopicName(result.resultMap.topicName);
        // topicid=result.resultMap.listQuestions.topicId;
        // console.log("My topic Id...>",topicid);
        // console.log("Topic Name",result.resultMap.topicName);
        // console.log("My result....", result);
        // console.log("***********************************************");
        // console.log("NAREN - MASTER");
     
      };
      useEffect(() => {
        questionDetails();
      }, []);
  return (
    <table>
        <thead>
            <tr>
                <th>TopicId</th>
                <th>Option A</th>
                <th>Option B</th>
                <th>Option C</th>
                <th>Option D</th>
                <th>Option E</th>
                <th>Answer</th>
                <th>Question Detail</th>
            </tr>
        </thead>
        <tbody>
          
            <td>
                {data.optionA}
              
            </td>
            <td>
            {data.optionB}
            </td>
            <td> {data.optionC}</td>
            <td> {data.optionD}</td>
            <td>{data.optionE}</td>
            <td>{data.answer}</td>
            <td> {data.questionDetail}</td>
            <td>  {topicName}</td>
          </tbody>
        <button className="btn btn-primary">Back</button>
    </table>
  )
}

export default DetailsOfQuestion
