import "bootstrap/dist/css/bootstrap.css";
import useStateRef from 'react-usestateref'
import React from "react";

const Login = () => {

  const [hasError, setHasError] = useStateRef(false);

  function SubmittingForm(e) {
    console.log("Submitting form called...");
    const data = new FormData(e.target);
    setHasError(false);
    document.getElementById("p1").classList.add("d-none");
    document.getElementById("p2").classList.add("d-none");
 
    let username = data.get("username");
    let password = data.get("password");
  
    const myobject = Object.fromEntries(data.entries());
    Object.entries(myobject).map(([key, value], keyIndex) => {
      validateformlogin(key, value);
    });

    
  };
  const validateformlogin = (key, value) => {
    switch (key) {
      case "username":
        if (value == "" || value == null) {
          document.getElementById("p1").classList.remove("d-none");
          document.getElementById("p1").classList.add("d-block");
          document.getElementById("p1").innerHTML = "Please enter a username";
          setHasError(true);
        } else {
          let regexx = new RegExp("[a-z0-9]+@[a-z]+.[a-z]{2,3}");
          if (!regexx.test(value)) {
            document.getElementById("p1").innerHTML =
              "You have not using the validate name...";
            setHasError(true);
          }
        }
        break;
      case "password":
        if (value == "") {
          document.getElementById("Please enter a password");
          setHasError(true);

          // alert("Password should be not be empty.");
        }

        break;

      default: {
        console.log("hii");
      }
    }
  }

  return (
    <div className="row my-5">
      <div className="col-4"></div>
      <div className="col-4">
        <form onSubmit={()=>{SubmittingForm()}} className="form-horizontal">
          <div className="form-group">
            <label htmlFor="exampleInputEmail1" className="form-label float-start">
              Username:
            </label>
            <input
              type="text"
              className="form-control"
              id="exampleInputEmail1"
              aria-describedby="emailHelp"
              placeholder="Enter your username"
              name="username"
            />
            <p id="p1" className="d-none text-dark"></p>
          </div>
          <div className="form-group">
            <label htmlFor="exampleInputPassword1" className="form-label float-start">
              Password:
            </label>
            <input
              type="password"
              className="form-control "
              id="exampleInputPassword1"
              placeholder="Password"
              name="password"
            />
            <p id="p2" className="d-none text-dark"></p>
          </div>

          <button type="submit" className="btn btn-primary mt-4">
            Submit
          </button>
        </form>
      </div>
      <div className="col-4"></div>
    </div>
  );
};

export default Login;
