import React, { useEffect, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";

const ExamTopicMappingView = () => {
  const params = useParams();
  const examId = params.examId;
  const topicId = params.topicId;
  console.log("topicId....>", topicId);
  console.log("param exam id...>", examId);
  const [data, setData] = useState("");
  const [topicData, setTopicData] = useState([]);
  const [Examname, SetexamName] = useState("");
  var ExamName=Examname;

  const showQuestionsForTopic = (topicId, examId) => {
    const topicIId = topicId;
    navigate(
      `/admin/updateExam/examdetails/question-topicView/${topicId}/${examId}`
    );
  };

  const navigate = useNavigate();
  console.log("admin Exam Id..", examId);


  const getExamTopicDetails=(examId,topicId)=>{

    examTopicDetails(examId,topicId);
  }

   async function examTopicDetails(examId,topicId){

    console.log("getExamTopicDetails called......");
    const map={
      examId : examId,
      topicId : topicId,
    }
    const fetchResponse = await fetch(
      "https://localhost:8443/onlineexam/control/examTopicRetrieveEvent",
      {
        method: "POST",
        credentials: "include",
        body: JSON.stringify(map),
        headers: {
          "Content-Type": "application/json"
        },
        Accept: "application/json",
      }
    )

      const outputResult=await fetchResponse.json();
      let ExamId=outputResult.resultMap.examId;
      console.log("ExamId....>",ExamId);


    }
    

 


  const deleteTopic=(examId,topicId)=>{
    let map={
      examId : examId,
      topicId : topicId
    }
  
  const response =  fetch(
    "https://localhost:8443/onlineexam/control/deleteExamTopicMappingEvent",
    {
      method: "DELETE",
      credentials: "include",
      body: JSON.stringify(map),
      headers: {
        "Content-type": "application/json",
      },
    }
  ).then(response=>{
    window.location.reload();
  })

  
};

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("Handle submit called....>")
    const formElement = document.getElementById("form");
    const data = new FormData(e.target);

    console.log("examId-----", examId);
    const topicId = data.get("topicId");
    const topicName = data.get("topicName");
    const percentage = data.get("percentage");
    const topicPassPercentage = data.get("topicPassPercentage");
    const questionsPerExam = data.get("questionsPerExam");
  
    const map = {
      examId: examId,
      topicId: topicId,
      topicName: topicName,
      percentage: percentage,
      topicPassPercentage: topicPassPercentage,
      questionsPerExam: questionsPerExam,
    };

    console.log("map.......>",map);

    const response = fetch(
      "https://localhost:8443/onlineexam/control/createOrUpdateExamTopicMappingMasterEvent",
      {
        method: "POST",
        credentials: "include",
        body: JSON.stringify(map),
        headers: {
          "Content-Type": "application/json"
        },
        Accept: "application/json",
      }
    )
      .then((response) => 
      {
        response.json()
        console.log("Response...>",response);
      })
      .then((data) => {
        console.log("Hey data.....>", data);
        window.location.reload();
      })
      .catch((err) => console.log("ERROR FROM FETCH", err));
  };

  async function examDetails() {
    console.log("exam Details is called....");
    console.log(examId);
    let map = {
      examId: examId,
    };
    console.log("map....", map);
    const response = await fetch(
      "https://localhost:8443/onlineexam/control/getExamTopicEvent",
      {
        method: "POST",
        credentials: "include",
        body: JSON.stringify(map),
        headers: {
          "Content-type": "application/json",
        },
      }
    );

    const result = await response.json();
    console.log("myresult.....", result);
    console.log("result...", result.ExamTopicNameMapping.ExamTopicMapping);
    const output = result.ExamTopicNameMapping.ExamTopicMapping;

    SetexamName(result.genericvalue.examName);
    console.log("ExamName....>",ExamName);
    console.log("output..........", output);
    output.map((obj, value) => {
      console.log("...", obj.examName);
      console.log(obj.examName);
      SetexamName(obj.examName);
    });
    
    const show_result = result.ExamTopicNameMapping.ExamTopicMapping;
    setTopicData(show_result);
    console.log("topicData....", topicData);
    console.log("examName.............", Examname);
  }
  useEffect(() => {
    examDetails();
  }, []);

  function showForm() {
    console.log("Show form called...");
    document.getElementById("myform").classList.remove("d-none");
    document.getElementById("myform").classList.add("d-block");
  }

  function hideForm() {
    console.log("Show form called...");
    document.getElementById("myform").classList.remove("d-none");
    document.getElementById("myform").classList.add("d-block");
  }

  return (
    <div className="container">
      <div className="container my-5">
        <table className="table table-striped">
          <thead>
            <tr>
              <th>Exam Name</th>
              <th>Topic Name</th>
              <th>Topic Pass Percentage</th>
              <th>Percentage</th>
              <th>Questions Per Exam</th>
              <th>Edit</th>
              <th>View Question</th>
              <th>
                <button className="btn btn-primary" onClick={() => showForm()}>
                  Add Topic
                </button>
              </th>
            </tr>
          </thead>
          <tbody>
            {topicData.length !== 0 ? (
              topicData.map((obj, value) => {
                // examname = { value };
                return (
                  <tr >
                    <td>{obj.examName}</td>
                    <td>{obj.topicName}</td>
                    <td>{obj.topicPassPercentage}</td>
                    <td>{obj.questionsPerExam}</td>
                    <td>{obj.percentage}</td>
                    <td>
                      <button className="btn btn-primary"  onClick={()=>getExamTopicDetails(obj.examId,obj.topicId)}>Edit</button>
                    </td>
                    <td>
                      <button
                        className="btn btn-primary"
                        onClick={() =>
                          showQuestionsForTopic(obj.topicId, obj.examId)
                        }
                      >
                        View
                      </button>
                    </td>
                    <td>
                      <button className="btn btn-danger" onClick={()=>deleteTopic(obj.examId,obj.topicId)} >Delete</button>
                    </td>
                  </tr>
                );
              })
            ) : (
              
              <h1 className="w-100" align="center">
                No topics to show
              </h1>
            
            )}
          </tbody>
        </table>
        <div>
          <button
            className="btn btn-primary"
            onClick={() => navigate("/admin/updateExam")}
          >
            Back
          </button>
        </div>
      </div>

      {/* ----------------add topic------------- */}

      <div>
        <div className="d-none" id="myform">
          <div className="col-10">
            <form action="" onSubmit={handleSubmit} id="form">
              <div className="mb-3">
                <label
                  htmlFor="exampleInputEmail1"
                  className="form-label float-start"
                >
                  Exam Name:
                </label>
                <input
                  id="examid"
                  className="form-control "
                  placeholder="ExamId * eg(1)"
                  name="examName"
                  value={ExamName}
                  readOnly
                />
                <p id="p1" className="text-danger"></p>
              </div>

              <div className="mb-3">
                <label
                  htmlFor="exampleInputEmail1"
                  className="form-label float-start"
                >
                  Topic Id:
                </label>
                <input
                  type="text"
                  id="examid"
                  className="form-control "
                  placeholder="Topicid"
                  name="topicId"
                />
                <p id="p1" className="text-danger"></p>
              </div>

              <div className="mb-3">
                <label
                  htmlFor="exampleInputEmail1"
                  className="form-label float-start"
                >
                  Topic Name:
                </label>
                <input
                  type="text"
                  id="examid"
                  className="form-control "
                  placeholder="TopicName"
                  name="topicName"
                  // value={examid}
                  // onChange={(e) => setexamid(e.target.value)}
                />
                <p id="p1" className="text-danger"></p>
              </div>
              <div className="mb-3">
                <label
                  htmlFor="exampleInputEmail1"
                  className="form-label float-start"
                >
                  Percentage:
                </label>
                <input
                  type="text"
                  id="examid"
                  className="form-control "
                  placeholder="Percentage * eg(100%)"
                  name="percentage"
                />
                <p id="p1" className="text-danger"></p>
              </div>
              <div className="mb-3">
                <label
                  htmlFor="exampleInputEmail1"
                  className="form-label float-start"
                >
                  TopicPassPercentage:
                </label>
                <input
                  type="text"
                  id="examid"
                  className="form-control "
                  placeholder="Topic Pass Percentage * eg(25%)"
                  name="topicPassPercentage"
                  
                />
                <p id="p1" className="text-danger"></p>
              </div>
              <div className="mb-3">
                <label
                  htmlFor="exampleInputEmail1"
                  className="form-label float-start"
                >
                  QuestionsPerExam:
                </label>
                <input
                  type="text"
                  id="examid"
                  className="form-control"
                  placeholder="Questions per Exam * eg(60)"
                  name="questionsPerExam"
                
                />
                <p id="p1" className="text-danger"></p>
              </div>
              <div className="mb-3">
                <button className="btn btn-primary" onClick={() => hideForm()}>
                  Submit
                </button>
              </div>
            </form>
          </div>
        </div>
      </div>


      {/* -------------------modal----------------- */}

<div class="modal fade" id="exampleModal" tabindex="-1" role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true">
  <div class="modal-dialog" role="document">
    <div class="modal-content">
      <div class="modal-header">
        <h5 class="modal-title" id="exampleModalLabel">Edit topic</h5>
        <button type="button" class="close" data-dismiss="modal" aria-label="Close">
          {/* <span aria-hidden="true">&times;</span> */}close
        </button>
      </div>
      <div class="modal-body">
      <form action="" onSubmit={handleSubmit} id="form">
              <div className="mb-3">
                <label
                  htmlFor="exampleInputEmail1"
                  className="form-label float-start"
                >
                  Exam Name:
                </label>
                <input
                  id="examid"
                  className="form-control "
                  placeholder="ExamId * eg(1)"
                  name="examName"
                  value={ExamName}
                  readOnly
                />
                <p id="p1" className="text-danger"></p>
              </div>

              <div className="mb-3">
                <label
                  htmlFor="exampleInputEmail1"
                  className="form-label float-start"
                >
                  Topic Id:
                </label>
                <input
                  type="text"
                  id="examid"
                  className="form-control "
                  placeholder="Topicid"
                  name="topicId"
                />
                <p id="p1" className="text-danger"></p>
              </div>

              <div className="mb-3">
                <label
                  htmlFor="exampleInputEmail1"
                  className="form-label float-start"
                >
                  Topic Name:
                </label>
                <input
                  type="text"
                  id="examid"
                  className="form-control "
                  placeholder="TopicName"
                  name="topicName"
                  // value={examid}
                  // onChange={(e) => setexamid(e.target.value)}
                />
                <p id="p1" className="text-danger"></p>
              </div>
              <div className="mb-3">
                <label
                  htmlFor="exampleInputEmail1"
                  className="form-label float-start"
                >
                  Percentage:
                </label>
                <input
                  type="text"
                  id="examid"
                  className="form-control "
                  placeholder="Percentage * eg(100%)"
                  name="percentage"
                />
                <p id="p1" className="text-danger"></p>
              </div>
              <div className="mb-3">
                <label
                  htmlFor="exampleInputEmail1"
                  className="form-label float-start"
                >
                  TopicPassPercentage:
                </label>
                <input
                  type="text"
                  id="examid"
                  className="form-control "
                  placeholder="Topic Pass Percentage * eg(25%)"
                  name="topicPassPercentage"
                  // value={examid}
                  // onChange={(e) => setexamid(e.target.value)}
                />
                <p id="p1" className="text-danger"></p>
              </div>
              <div className="mb-3">
                <label
                  htmlFor="exampleInputEmail1"
                  className="form-label float-start"
                >
                  QuestionsPerExam:
                </label>
                <input
                  type="text"
                  id="examid"
                  className="form-control"
                  placeholder="Questions per Exam * eg(60)"
                  name="questionsPerExam"
                  // value={examid}
                  // onChange={(e) => setexamid(e.target.value)}
                />
                <p id="p1" className="text-danger"></p>
              </div>
              <div className="mb-3">
                <button className="btn btn-primary offset-9" data-dismiss="modal">
                  Submit
                </button>
              </div>
            </form>
      </div>
     
    </div>
  </div>
</div>
    </div>
  );
};

export default ExamTopicMappingView;
