import React, { useEffect } from "react";
import { useState } from "react";

const ShowExams = () => {
  const [data, setData] = useState("");
  const [topicData,setTopicData]=useState("");

  const deleteRows = (index) => {
    const updatedRows = [...data];
    updatedRows.splice(index, 1);
    setData(updatedRows);
    // fetch("https://localhost:8443/onlineexam/control/deleteexam", {
    //   method: "DELETE",
    // });
  };

  


  const examDetails=(examId)=>{
    fetch("https://localhost:8443/onlineexam/control/getExamTopicEvent",{
      method: 'POST',
      body: JSON.stringify({examId}),
    }).then(response=>{
      const text=response.json();
      setData(text);
    });
  }

  async function fetchInfo() {
    console.log("fetch obj is called....");

    const response = await fetch(
      "https://localhost:8443/onlineexam/control/examMasterListEvent"
    );

    const text = await response.json();
    console.log(text);
    setData(text.ExamData)
   
  }

  useEffect(() => {
    fetchInfo();
  }, []);

  return (
    <div className="container">
      <div className="col-8 ">
        <table className="table table-striped ">
          <thead>
            <tr>
               <th>ExamId</th> 
              <th>ExamName</th>
              {/* <th>CreationDate</th>
              <th>ExpirationDate</th>
              <th>No of Questions</th>
              <th>Duration Minutes</th>
              <th>Pass Percentage</th>
              <th>Questions Randomized</th>
              <th>Negative Mark value</th> */}
              <th></th>
              <th></th>
              <th></th>
              <th></th>
              
            </tr>
          </thead>

          <tbody>
            {data ? data.map((obj, value) => (
                  <tr>
                    <td>{obj.examId}</td> 
                    <td>{obj.examName}</td>
                     <td>{obj.description}</td> 
                    {/* <td>{obj.creationDate}</td>
                    <td>{obj.expirationFate}</td>
                    <td>{obj.noOfQuestions}</td>
                    <td>{obj.durationMinutes}</td>
                    <td>{obj.passPercentage}</td> */}
                    {/* <td>{obj.questionsRandomized}</td> */}
                    {/* <td>{obj.answersMust}</td> */}

                    {/* <td>{obj.enableNegativeMark}</td> */}
                    {/* <td>{obj.negativeMarkValue}</td> */}
                    <td>
                      <button className="btn btn-primary">Edit</button>
                    </td>
                    <td>
                      <button className="btn btn-primary" onClick={examDetails(obj.examId)}>Details</button>
                    </td>
              
                    <td>
                      <button
                        className="btn btn-danger"
                        onClick={() => deleteRows(value)}
                      >
                        Delete
                      </button>
                    </td>
                  </tr>
                ))
              : ""}
          </tbody>
        </table>
      </div>
    </div>
  );
};
export default ShowExams;
