import React, { useEffect, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";

const ExamTopicMappingView = () => {
  const params=useParams();
  const getexamId = params.examId;
  const topicId=params.topicId;
  console.log("topicId....>",topicId);
  console.log("param exam id...>",getexamId);
    const [data, setData] = useState("");
    const [topicData,setTopicData]=useState("");
    const [Examname,SetexamName]=useState("");
  
    const showQuestionsForTopic=(topicId,examId)=>{
        console.log("Show questions for topic called.....");
        const topicIId=topicId;
        console.log("This is the topic Id..." , topicIId);
        navigate(`/admin/updateExam/examdetails/question-topicView/${topicId}/${examId}`);
    }
    var examname = null;
   
    const navigate=useNavigate();
    console.log("admin Exam Id..",getexamId);

    const handleSubmit = async (e) => {
      e.preventDefault();
  
      const formElement = document.getElementById("myform");
      const data = new FormData(formElement);
      // handleErrors();
  
      const examId = data.get("examId");
      console.log("examId-----", getexamId);
      const topicId=data.get("topicId");
      const topicName=data.get("topicName");
      const percentage = data.get("percentage");
      const topicPassPercentage = data.get("topicPassPercentage");
      const questionsPerExam = data.get("questionsPerExam");
      const expirationDate = data.get("expirationDate");

  
      const myobject = Object.fromEntries(data.entries());
      Object.entries(myobject).map(([key, value], keyIndex) => {
        validateformexamtopicMappingmaster(key, value);
      });
  
      function validateformexamtopicMappingmaster(key, value) {
        switch (key) {
          case "examId": {
            if (value == "" || value == null) {
              // alert("examId should not be em")
            }
          }
        }
      }
  
      const map = {
        examId: examId,
        topicId: topicId,
        topicName: topicName,
        percentage: percentage,
        topicPassPercentage: topicPassPercentage,
        questionsPerExam: questionsPerExam,
      };
      console.log(map);
  
       const response= fetch("https://localhost:8443/onlineexam/control/createOrUpdateExamTopicMappingMasterEvent", {
          method: "POST",
          credentials: "include",
          body: JSON.stringify(map),
          headers: {
            "Content-Type": "application/json",
            Accept: "application/json",
            
          },
        })
          .then((response) =>
              alert("value added successfully...")
          ).then(data=>{
            console.log("Hey data.....>",data);
          })
          .catch((err) => console.log("ERROR FROM FETCH", err));
      
    };

    async function examDetails ()  {
      
      console.log("exam Details is called....");
      console.log(examId);
      let map={
         examId: examId,
      }
      console.log("map....",map);
      const response = await fetch(
        "https://localhost:8443/onlineexam/control/getExamTopicEvent",
        {
          method: "POST",
          credentials: "include",
          body: JSON.stringify(map),
          headers: {
            "Content-type": "application/json",
          },
        }
      );
  
      const result = await response.json();
      console.log("myresult.....",result);
      console.log("result...", result.ExamTopicNameMapping.ExamTopicMapping);
      const output = result.ExamTopicNameMapping.ExamTopicMapping;
      console.log("output..........", output);
      output.map((obj, value) => {
        console.log("Hey examName...",obj.examName);
        examname = obj.examName;
        console.log(examname);
        SetexamName(examname);
      });
      console.log("My exam Name....", examname);
      const myexamname = JSON.stringify(examname);
      console.log("myexamname..........", myexamname);
      examname = myexamname;
      setTopicData([...result.ExamTopicNameMapping.ExamTopicMapping]);
      console.log("topicData....", topicData);
  
      console.log("examName.............", examname);
    };
    useEffect(() => {
      examDetails();
    }, []);
   
  function showForm() {
    console.log("Show form called...");
    document.getElementById("myform").classList.remove("d-none");
    document.getElementById("myform").classList.add("d-block");
  }

  function hideForm() {
    console.log("Show form called...");
    document.getElementById("myform").classList.remove("d-none");
    document.getElementById("myform").classList.add("d-block");
  }


  return (
    <div className="container">
      
      <div className="container my-5">
        <table className="table table-striped">
          <thead>
            <tr>
              <th>Exam Name</th>
              <th>Topic Name</th>
              <th>Topic Pass Percentage</th>
              <th>Percentage</th>
              <th>Questions Per Exam</th>
              <th>Edit</th>
              <th>View Question</th>
              <th>
                <button className="btn btn-primary" onClick={() => showForm()}>
                  Add Topic
                </button>
              </th>
            </tr>
          </thead>
          <tbody>
            {topicData ? (
              topicData.map((obj, value) => {
                examname = { value };
                return (
                  <tr key={value}>
                    <td>{obj.examName}</td>
                    <td>{obj.topicName}</td>
                    <td>{obj.topicPassPercentage}</td>
                    <td>{obj.questionsPerExam}</td>
                    <td>{obj.percentage}</td>
                    <td><button className="btn btn-primary">Edit</button></td>
                    <td>
                      <button
                        className="btn btn-primary"
                         onClick={() => showQuestionsForTopic(obj.topicId,obj.examId)}
                      >
                        View
                      </button>
                    </td>
                    <td>
                      <button className="btn btn-danger">Delete</button>
                    </td>
                  </tr>
                );
              })
            ) : 
              <p>No data</p>
            }
