import React from "react";
import { useState, useEffect } from "react";
import { useNavigate, useParams } from "react-router-dom";
import "../ExamMaster/QuestionForTopicView.css";
import useStateRef from "react-usestateref";

const QuestionForTopicView = () => {
  const params = useParams();
  const topicId = params.topicId;
  const examId = params.examId;
  console.log("ExamId...>", examId);
  console.log("param exam id...>", topicId);
  const [questionData, setQuestionData] = useState([]);
  const [topicName, setTopicName] = useState("");
  const [hasError, setHasError, hasNoError] = useStateRef(false);
  const [data,setData]=useState("");
  const [questionId, setquestionId] = useState("");
  const [questionDetail, setquestionDetail] = useState("");
  const [optionA, setoptionA] = useState("");
  const [optionB, setoptionB] = useState("");
  const [optionC, setoptionC] = useState("");
  const [optionD, setoptionD] = useState("");
  const [optionE, setoptionE] = useState("");
  const [answer, setanswer] = useState("");
  const [numAnswers, setnumAnswers] = useState("");
  const [questionType, setquestionType] = useState("");
  const [difficultyLevel, setdifficultyLevel] = useState("");
  const [answerValue, setanswerValue] = useState("");
  const [TopicId, settopicId] = useState("");
  const [negativeMarkValue, setnegativeMarkValue] = useState("");

  const navigate = useNavigate();

  var topicid;

  const addQuestion=()=>{

    <div class="modal" tabindex="-1">
  <div class="modal-dialog">
    <div class="modal-content">
      <div class="modal-header">
        <h5 class="modal-title">Modal title</h5>
        <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
      </div>
      <div class="modal-body">
      <div className="container my-3">
      <div className="col-10 mx-auto">
      <legend className="bg-primary text-light border rounded-pill">Create Question </legend>
        <form className="d-flex" id="myform" >
          <div className="col-5">
            <div className="mb-3">
              <label
                htmlFor="exampleInputEmail1"
                className="form-label float-start"
              >
                QuestionId*:
              </label>
              <input
                type="text"
                id="questionId"
                className="form-control text-decoration-none"
                placeholder="questionId * eg(1)"
                min={0}
                name="questionId"
                // value={examid}
                 onChange={(e) => setquestionId(e.target.value)}
              />
              {/* <div class="mb-3">
                <label for="" class="form-label"></label>
                
              </div> */}

              <p id="p1" className="text-danger"></p>
            </div>
            <div className="mb-3">
              <label
                htmlFor="exampleInputEmail1"
                className="form-label float-start"
              >
                Question Detail:
              </label>
              {/* <input
                type="text"
                id="questionDetail"
                className="form-control"
                placeholder="Enter the Detail of the Question"
                name="questionDetail"
                // value={examname}
                // onChange={(e) => setexamname(e.target.value)}
              /> */}

              <textarea
                class="form-control"
                name="questionDetail"
                placeholder="questionDetail"
                id="questionDetail"
                rows="3"
                onChange={(e) => setquestionDetail(e.target.value)}
              ></textarea>
              <p id="p2" className="text-danger"></p>
            </div>
            <div className="mb-3">
              <label
                htmlFor="exampleInputEmail1"
                className="form-label float-start"
              >
                Option A:
              </label>
              <textarea
                class="form-control"
                name="optionA"
                placeholder="Option A"
                id="optionA"
                rows="1"
                onChange={(e) => setoptionA(e.target.value)}
              ></textarea>
              {/* <input
                type="text"
                id="optionA"
                className="form-control"
                placeholder="Option A"
                name="optionA"
                // value={description}
                // onChange={(e) => setdescription(e.target.value)}
              /> */}
              <p id="p3" className="text-danger"></p>
            </div>
            <div className="mb-3">
              <label htmlFor="" className="form-label float-start">
                Option B:
              </label>
              {/* <input
                type="text"
                id="optionB"
                className="form-control"
                name="optionB"
                placeholder="option B"
                // value={creationdate}
                // onChange={(e) => setcreationdate(e.target.value)}
              /> */}
              <textarea
                class="form-control"
                name="optionB"
                placeholder="option B"
                id="optionB"
                rows="1"
                onChange={(e) => setoptionB(e.target.value)}
              ></textarea>

              <p id="p4" className="text-danger"></p>
            </div>
            <div className="mb-3">
              <label
                htmlFor="exampleInputEmail1"
                className="form-label float-start"
              >
                optionC:
              </label>
              {/* <input
                type="text"
                id="optionC"
                className="form-control"
                name="optionC"
                placeholder="option C"
                // value={expirationdate}
                // onChange={(e) => setexpirationdate(e.target.value)}
              /> */}
              <textarea
                class="form-control"
                name="optionC"
                placeholder="option C"
                id="optionC"
                rows="1"
                onChange={(e) => setoptionC(e.target.value)}
              ></textarea>

              <p id="p5" className="text-danger"></p>
            </div>
            <div className="mb-3">
              <label
                htmlFor="exampleInputPassword1"
                className="form-label float-start"
              >
                Option D:
              </label>
              {/* <input
                type="text"
                id="optionD"
                className="form-control"
                placeholder="Option D"
                name="optionD"
                // value={noofquestions}
                // onChange={(e) => setnoofquestions(e.target.value)}
              /> */}
              <textarea
                class="form-control"
                name="optionD"
                placeholder="option D"
                id="optionD"
                rows="1"
                onChange={(e) => setoptionD(e.target.value)}
              ></textarea>

              <p id="p6" className="text-danger"></p>
            </div>
            <div className="mb-3">
              <label
                htmlFor="exampleInputPassword1"
                className="form-label float-start"
              >
                Option E:
              </label>
              {/* <input
                type="text"
                id="optionE"
                className="form-control"
                placeholder="Option E"
                name="optionE"
                // value={noofquestions}
                // onChange={(e) => setnoofquestions(e.target.value)}
              /> */}
              <textarea
                class="form-control"
                name="optionE"
                placeholder="option E"
                id="optionE"
                rows="1"
                onChange={(e) => setoptionE(e.target.value)}
              ></textarea>

              <p id="p7" className="text-danger"></p>
            </div>
          </div>

          <div className="col-5 offset-1">
            <div className="mb-3">
              <label
                htmlFor="exampleInputEmail1"
                className="form-label float-start"
              >
                Answer :
              </label>
              {/* <input
                type="text"
                id="answer"
                placeholder="answer"
                name="answer"
                className="form-control"
                // value={durationminutes}
               onChange={(e) => setdurationminutes(e.target.value)}
              /> */}
              <textarea
                class="form-control"
                name="answer"
                placeholder="answer "
                id="answer"
                rows="3"
                onChange={(e) => setanswer(e.target.value)}
              ></textarea>

              <p id="p8" className="text-danger"></p>
            </div>
            <div className="mb-3">
              <label
                htmlFor="exampleInputEmail1"
                className="form-label float-start"
              >
                NumAnswers:
              </label>
              <input
                type="text"
                id="numAnswers"
                placeholder="num of answers"
                name="numAnswers"
                className="form-control"
                // value={passpercentage}
                onChange={(e) => setnumAnswers(e.target.value)}
              />
              <p id="p9" className="text-danger"></p>
            </div>
            <div className="mb-3">
              <label
                htmlFor="exampleInputEmail1"
                className="form-label float-start"
              >
                Question Type:
              </label>

              <select
                class="form-select"
                id="select"
                aria-label="Default select example"
                onSelect={(questionType)=>setquestionType(questionType.value)}
              >
                <option id="questionType" value="singleChoice">
                  Single Choice
                </option>
                <option id="questionType" value="multipleChoice">
                  Multiple Choice
                </option>
                <option id="questionType" value="trueorFalse">
                  True or False
                </option>
                <option id="questionType" value="fillintheblankhs">
                  Fill in the Blankhs
                </option>
                <option id="questionType" value="answerindetail">
                  Answer in Detail
                </option>
                {/* <option value="3">Three</option> */}
              </select>
            </div>
            <p id="p10" className="text-danger"></p>

            <div className="mb-3">
              <label
                htmlFor="exampleInputEmail1"
                className="form-label float-start"
              >
                Difficulty Level:
              </label>
              <input
                type="text"
                name="difficultyLevel"
                placeholder="difficulty level"
                id="difficultyLevel"
                className="form-control"
                // value={answersmust}
                onChange={(e) => setdifficultyLevel(e.target.value)}
              />
              <p id="p11" className="text-danger"></p>
            </div>
            <div className="mb-3">
              <label
                htmlFor="exampleInputEmail1"
                className="form-label float-start"
              >
                Answer Value:
              </label>
              <input
                type="text"
                name="answerValue"
                placeholder="answer value"
                id="answerValue"
                className="form-control"
                // value={enablenegativemark}
                onChange={(e) => setanswerValue(e.target.value)}
              />
              <p id="p12" className="text-danger"></p>
            </div>
            <div className="mb-3">
              <label
                htmlFor="exampleInputEmail1"
                className="form-label float-start"
              >
                Topic:
              </label>
              {/* <input
                type="text"
                placeholder="topic Id"
                id="topicId"
                name="topicId"
                className="form-control"
                // value={negativemarkvalue}
                onChange={(e) => settopicId(e.target.value)}
              /> */}
              
              <select name=""  id="">Topic 
               
                { data ? data.map((obj,value)=>{
                           
                    return(
                              <option value={obj.topicId}>Topic Id-{obj.topicId} Topic Name- {obj.topicName}</option>
                      )
                }):""}
                </select>
          
              <p id="p13" className="text-danger"></p>
            </div>

            <div className="mb-3">
              <label
                htmlFor="exampleInputEmail1"
                className="form-label float-start"
              >
                NegativeMarkValue:
              </label>
              <input
                type="text"
                placeholder="Negativemarkvalue eg(0.25)"
                id="negativeMarkValue"
                name="negativeMarkValue"
                className="form-control"
                // value={negativemarkvalue}
               onChange={(e) => setnegativeMarkValue(e.target.value)}
              />
              <p id="p14" className="text-danger"></p>
              
            </div>
          <div>
            
              <button type="submit" className="btn btn-primary ">
                Finish
              </button>
            <button type="submit" className="btn btn-primary offset-1">
                Add Question
              </button>
            
            </div>
          </div>
        </form>
      </div>
    </div>

      </div>
      <div class="modal-footer">
        <button type="button" class="btn btn-secondary" data-bs-dismiss="modal">Close</button>
        <button type="button" class="btn btn-primary">Save changes</button>
      </div>
    </div>
  </div>
</div>
  }

  async function QuestionDetails() {
    console.log("QuestionForTopicView  is called....");
    // console.log(topicId);
    const map = {
      topicId: topicId,
    };

    const response = await fetch(
      "https://localhost:8443/onlineexam/control/viewQuestions",
      {
        method: "POST",
        credentials: "include",
        body: JSON.stringify(map),
        headers: {
          "Content-type": "application/json",
        },
      }
    );

    const result = await response.json();
    // console.log(result);
    const myobj = result.resultMap.listQuestions;
    const topicname = result.resultMap.topicName;
    let resultvalue = {
      myobj: myobj,
      topicname: topicname,
    };
    // console.log("result", result.resultMap.listQuestions);
    // console.log("second result,", result.resultMap.topicName);
    const ouput_questions = result.resultMap.listQuestions;
    if (ouput_questions === null && ouput_questions === "") {
      return (
        <div>
          <p>There is no data to show</p>
        </div>
      );
    } else {
      setQuestionData([...ouput_questions]);
      console.log(ouput_questions);
    }

    // // setQuestionData([ ...resultvalue]);
    // console.log("result value......", resultvalue);
    // console.log("question data....", questionData);
    setTopicName(result.resultMap.topicName);
    // topicid=result.resultMap.listQuestions.topicId;
    // console.log("My topic Id...>",topicid);
    // console.log("Topic Name",result.resultMap.topicName);
    // console.log("My result....", result);
    // console.log("***********************************************");
    // console.log("NAREN - MASTER");
  }
  useEffect(() => {
    QuestionDetails();
  }, []);
  return (
    <div className="row ">
      <h6 className="text-align-center my-3" align="center">TopicName:{topicName}</h6>
      
      
      <button className="btn btn-primary w-25 align-items-right offset-8" 
      data-bs-toggle=""
      data-bs-target
      onClick={addQuestion(topicId)}>Add Question</button>

      <div className="col-12 my-5">
        {questionData ? (
          questionData.map((obj, value) => {
            
            return (
              <div className="d-flex striped">
                <div className="col-8">
                  <table className="table table-striped">
                    <tr>
                      <td>
                        {" "}
                        <div key={value}>Question: {obj.questionDetail}</div>
                      </td>
                    </tr>
                  </table>
                </div>
                <div className="col-4">
                  <button className="btn btn-primary btn1"    onClick={()=>navigate(`/admin/updateExam/examdetails/question-topicView/view-questions/${obj.questionId}/${topicName}/${examId}`)} 
>View Details</button>
                  <button className="btn btn-danger">Delete</button>

                </div>

              
              </div>
            );
          })
        ) : (
          <p>No data</p>
        )}
      </div>
                  <button className="btn btn-primary w-25 offset-4 my-3" onClick={()=>navigate(`/admin/updateExam/examdetails/${examId}`)}>Back</button> 


    </div>
  );
};

export default QuestionForTopicView;
