      data.examId !==examId
    }));
  }

  const deleteRows = (index) => {
    const updatedRows = [...data];
    updatedRows.splice(index, 1);
    setRows(updatedRows);
};



  async function fetchInfo() {
    console.log("fetch obj is called....");

    const response = await fetch(
      "https://localhost:8443/onlineexam/control/examMasterListEvent"
    );
 
    const text = await response.json();
   
    setData(text.examData);
    console.log("myexamdata..", text.examData);
  }

  useEffect(() => {
    fetchInfo();
  }, []);

  return (
    <div className="container">
      <div className="col-8">
        <table className="table table-striped">
          <thead>
            <tr>
              <th>ExamId</th>
              <th>ExamName</th>
              {/* <th>Description</th> */}
              <th>CreationDate</th>
              <th>ExpirationDate</th>
              <th>No of Questions</th>
              <th>Duration Minutes</th>
              <th>Pass Percentage</th>
              <th>Questions Randomized</th>
              {/* <th>Answers Must</th> */}
              {/* <th>Enable negative Mark</th> */}
              <th>Negative Mark value</th>
            </tr>
          </thead>

          <tbody>
            {data
              ? data.map((obj, value) => (
                  <tr>
                    <td>{obj.examId}</td>
                    <td>{obj.examName}</td>
                    {/* <td>{obj.description}</td> */}
                    <td>{obj.creationDate}</td>
                    <td>{obj.expirationFate}</td>
                    <td>{obj.noOfQuestions}</td>
                    <td>{obj.durationMinutes}</td>
                    <td>{obj.passPercentage}</td>
                    <td>{obj.questionsRandomized}</td>
                    {/* <td>{obj.answersMust}</td> */}

                    {/* <td>{obj.enableNegativeMark}</td> */}
