import React, { useEffect } from "react";
import { useState } from "react";
import "../ExamMaster/ShowExams.css";
import { Link, Outlet, useNavigate } from "react-router-dom";
import ExamTopicMappingView from "./ExamTopicMappingView";
import CreateExamMaster from "./CreateExamMaster";

const ShowExams = () => {
  const [data, setData] = useState("");
  const [topicData, setTopicData] = useState([]);
  const [Examname, SetexamName] = useState("");
  var ExamId=6;

  const navigate = useNavigate();
  const deleteRows = (examId) => {
    const updatedRows = [...data];
    updatedRows.splice(examId, 1);
    setData(updatedRows);
    let map={
      examId : examId,
    }
    // const response = fetch(
    //   "https://localhost:8443/onlineexam/control/DeleteExamMasterEvent",
    //   {
    //     method: "DELETE",
    //     credentials: "include",
    //     body: JSON.stringify(map),
    //     headers: {
    //       "Content-type": "application/json",
    //     },
    //   }
    // );

    // const result = response
    //   console.log("result....>",result);
  };
  

  

  const showexamDetails = (examId) => {
    console.log("Hii show examDetails called...");
    const examIdd = examId;
    localStorage.setItem("examId", examIdd);
    console.log(examIdd);
    ExamId=examIdd;
     navigate('/admin/updateExam/examdetails');
    // <Link to="examdetails"/>
    // <Link to="examdetails"></Link>
    // <Link to='/admin/createExam' state={}></Link>
  };

  const ExamDetails =(examId)=>{

    console.log("examId",examId);
    navigate(`/admin/createExam?examId=${examId}`);
  }

  function fetchInfo() {


    console.log("fetch obj is called....");

    const response =  fetch(
      "https://localhost:8443/onlineexam/control/examMasterListEvent"
    ).then(response=>{
      return response.json();
    })

    const text =  response.json();
    console.log(text);
    setData(text.ExamData);
    const res = text.ExamData;
    const result=JSON.stringify(res);
    console.log("res....",result);

  }

  useEffect(() => {
    fetchInfo();
  }, []);

  return (
    <div className="container  my-5">
      <div className="col-10 offset-1">
        <table className="table table-striped ">
          <thead>
            <tr>
              <th>ExamName</th>
              <th></th>
              <th>Edit</th>
              <th>Details</th>
              <th>Delete</th>
            </tr>
          </thead>

          <tbody>
            {data
              ? data.map((obj, value) => (
                
                  <tr key={obj.examId}> 
          
                    <td >{obj.examName}</td>
                    <td>{obj.description}</td>
        
                    <td>
                      <button
                        className="btn btn-primary"
                        onClick={() => ExamDetails(obj.examId)}
                      >
                        Edit
                      </button>
                    </td>
                    <td>
                      <button
                        className="btn btn-primary"
                        // onClick={() => {
                        //   showexamDetails(obj.examId);
                        // }}
                        onClick={() => navigate(`examdetails/${obj.examId}`)}

                      >
                        Details
                      </button>
                      {/* <button className="btn btn-primary" onClick={examDetails(obj.examId)}>Details</button> */}
                    </td>
                    <td>
                      <button
                        className="btn btn-danger"
                        onClick={() => deleteRows(obj.examId)}
                      >
                        Delete
                      </button>
                    </td>
            
                  </tr>
                 
                ))
              : <p>No records to show</p>}
          </tbody>
        </table>
       
      </div>
      
      <div className="col-4">
        <Outlet/>
      </div>
     
    </div>
  );
};
export default ShowExams;
