import React from 'react'

const QuestionForTopicView = () => {

    const [questionData, setQuestionData] = useState([]);
    const [questionName, setQuestionName] = useState("");
   

  async function questionDetails()  {
        console.log("question Details is called....");
        const topicId=localStorage.getItem("topicId");
        console.log(topicId);
        const map = {
          topicId: topicId,
        };
    
        const response = await fetch(
          "https://localhost:8443/onlineexam/control/viewQuestions",
          {
            method: "POST",
            credentials: "include",
            body: JSON.stringify(map),
            headers: {
              "Content-type": "application/json",
            },
          }
        );
    
        const result = await response.json();
        console.log(result);
        const myobj = result.resultMap.listQuestions;
        const topicname = result.resultMap.topicName;
        let resultvalue = {
          myobj: myobj,
          topicname: topicname,
        };
        console.log("result", result.resultMap.listQuestions);
        console.log("second result,", result.resultMap.topicName);
        setQuestionData([...result.resultMap.listQuestions]);
        // setQuestionData([ ...resultvalue]);
        console.log("result value......", resultvalue);
        console.log("question data....", questionData);
        setQuestionName(result.resultMap.topicName);
        console.log("Topic Name",result.resultMap.topicName);
    
        console.log("My result....", result);
    
        console.log("***********************************************");
        console.log("NAREN - MASTER");
      };
      useEffect(() => {
        questionDetails();
      }, []);


  return (
    <div>
         <div className="container my-5">
        <table className="table table-striped">
          <thead>
            <tr>
       
              <th></th>
           
              <th>TopicName</th>
              <th><button className="btn btn-primary">Add Question</button></th>
            </tr>
          </thead>
          <tbody>
            {questionData ? (
              questionData.map((obj, value) => {
                examname = { value };
                return (
                  <tr key={value}>
              
                    <td>{obj.optionA}</td>
                    <td>{obj.optionB}</td>
                    <td>{questionName}</td>
                    
                    {/* <td>{obj.topicName}</td>
                  <td>{obj.topicPassPercentage}</td>
                  <td>{obj.questionsPerExam}</td>
                  <td>{obj.percentage}</td> */}

                    {/* <td><button className="btn btn-primary" onClick={() => showQuestionsForTopic(obj.topicId)}>View Question</button></td>
                  <td><button className="btn btn-danger">Delete</button></td>
                   */}
                  </tr>
                );
              })
            ) : (
              <>No data</>
            )}
          </tbody>
          <tfoot></tfoot>
        </table>
      </div>
    </div>
  )
}

export default QuestionForTopicView
