import { useEffect, useState } from "react";
import Image1 from "../Header/images.png";
import 'bootstrap/dist/css/bootstrap.css';
const name = "Flash";
const currDate = new Date().toLocaleDateString();
const currTime = new Date().toLocaleTimeString();

const Header = () => {
  
  const [currentTime,SetcurrentTime]=useState(currTime.seconds);
  const [time, setTime] = useState({
    minutes: new Date().getMinutes(),
    hours: new Date().getHours(),
    seconds: new Date().getSeconds()
  })

  
  useEffect(() => {
    const intervalId = setInterval(() => {
      const date = new Date();
      setTime({
        minutes: date.getMinutes(),
        hours: date.getHours(),
        seconds: date.getSeconds()
      })
    }, 1000)
    return () => clearInterval(intervalId);
  }, [])

  const convertToTwoDigit = (number) => {
    return number.toLocaleString('en-US', {
      minimumIntegerDigits: 2
    })
  }

  return (
    <div>
      <header className="header">
        
      </header> 



      <nav className="navbar navbar-expand-lg bg-primary sticky-top navbar-dark py-3">
      <div className="container">
        <img className="img1" src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRFUyjXzzx6DUuFOdld6t7ToxT__gUxxkjfOPv-nNuvayL4L3jmQuQ2r6EIskGYFZ715UY&usqp=CAU" height={50} alt="" /> 
         <p  className="navbar-brand offset-1 mt-2">NPM TEAM</p>
         <p  className="navbar-brand offset-1 mt-2 offset-2 font-5">ONLINE EXAMINATION</p>
        <div className="collapse navbar-collapse" id="navmenu">
          <ul className="navbar-nav ms-auto">
            <li className="nav-item">
              <p className="text-light mt-3">{currDate} </p>
              {/* <p>{currTime}</p> */}
            </li>
          </ul>
        </div>

        <div className='clock'>
      <span className="text-light">{convertToTwoDigit(time.hours)}:</span>
      <span>{convertToTwoDigit(time.minutes)}:</span>
      <span>{convertToTwoDigit(time.seconds)}</span>
      <span>{time.hours >= 12 ? ' PM' : ' AM'}</span>
    </div>


       
        {/* <p className="p1">{currTime}</p> */}
  
      </div>
    </nav>
     
    </div>
  );
};

export default Header;
