import React, { useEffect, useState } from "react";

const ExamTopicMappingView = () => {
    const [data, setData] = useState("");
    const [topicData,setTopicData]=useState("");
    var examname = null;
    const adminExamId=localStorage.getItem("examId");
    console.log("admin Exam Id..",adminExamId);

    const examDetails = async (adminExamId) => {
      console.log("exam Details is called....");
      console.log(adminExamId);
      const response = await fetch(
        "https://localhost:8443/onlineexam/control/getExamTopicEvent",
        {
          method: "POST",
          credentials: "include",
          body: JSON.stringify(adminExamId),
          headers: {
            "Content-type": "application/json",
          },
        }
      );
  
      const result = await response.json();
      console.log(result);
      console.log("result...", result.ExamTopicNameMapping.ExamTopicMapping);
      const output = result.ExamTopicNameMapping.ExamTopicMapping;
      console.log("output..........", output);
      output.map((obj, value) => {
        examname = obj.examName;
        console.log(examname);
        SetexamName(examname);
      });
      console.log("My exam Name....", examname);
      const myexamname = JSON.stringify(examname);
      console.log("myexamname..........", myexamname);
      examname = myexamname;
      setTopicData([...result.ExamTopicNameMapping.ExamTopicMapping]);
      console.log("topic data...", result.ExamTopicNameMapping.ExamTopicMapping);
  
      console.log("***********************************************");
      console.log("NAREN - MASTER");
  
      console.log("topicData....", topicData);
  
      console.log("examName.............", examname);
    };
   
  function showForm() {
    console.log("Show form called...");
    document.getElementById("myform").classList.remove("d-none");
    document.getElementById("myform").classList.add("d-block");
  }

  function hideForm() {
    console.log("Show form called...");
    document.getElementById("myform").classList.remove("d-none");
    document.getElementById("myform").classList.add("d-block");
  }

  return (
    <div className="container">
      
      <div className="container my-5">
        <table className="table table-striped">
          <thead>
            <tr>
              <th>Exam Name</th>
              <th>Topic Name</th>
              <th>Topic Pass Percentage</th>
              <th>Percentage</th>
              <th>Questions Per Exam</th>
              <th>View Question</th>
              <th>
                <button className="btn btn-primary" onClick={() => showForm()}>
                  Add Topic
                </button>
              </th>
            </tr>
          </thead>
          <tbody>
            {topicData ? (
              topicData.map((obj, value) => {
                examname = { value };
                return (
                  <tr key={value}>
                    <td>{obj.examName}</td>
                    <td>{obj.topicName}</td>
                    <td>{obj.topicPassPercentage}</td>
                    <td>{obj.questionsPerExam}</td>
                    <td>{obj.percentage}</td>

                    <td>
                      <button
                        className="btn btn-primary"
                        onClick={() => showQuestionsForTopic(obj.topicId)}
                      >
                        View Question
                      </button>
                    </td>
                    <td>
                      <button className="btn btn-danger">Delete</button>
                    </td>
                  </tr>
                );
              })
            ) : (
              <>No data</>
            )}
          </tbody>
          <tfoot></tfoot>
        </table>
      </div>


      {/* ----------------add topic------------- */}

      <div>
        <div className="d-none" id="myform">
          <div className="col-10">
            <form action="">
              <div className="mb-3">
                <label
                  htmlFor="exampleInputEmail1"
                  className="form-label float-start"
                >
                  Exam Name:
                </label>
                <input
                  id="examid"
                  className="form-control "
                  placeholder="ExamId * eg(1)"
                  name="examId"
                  value={Examname}
                  readOnly

                  // value={examid}
                  // onChange={(e) => setexamid(e.target.value)}
                />
                <p id="p1" className="text-danger"></p>
              </div>
              <div className="mb-3">
                <label
                  htmlFor="exampleInputEmail1"
                  className="form-label float-start"
                >
                  Topic Name:
                </label>
                <input
                  type="text"
                  id="examid"
                  className="form-control "
                  placeholder="TopicName"
                  name="examId"
                  // value={examid}
                  // onChange={(e) => setexamid(e.target.value)}
                />
                <p id="p1" className="text-danger"></p>
              </div>
              <div className="mb-3">
                <label
                  htmlFor="exampleInputEmail1"
                  className="form-label float-start"
                >
                  Percentage:
                </label>
                <input
                  type="text"
                  id="examid"
                  className="form-control "
                  placeholder="Percentage * eg(100%)"
                  name="examId"
                  // value={examid}
                  // onChange={(e) => setexamid(e.target.value)}
                />
                <p id="p1" className="text-danger"></p>
              </div>
              <div className="mb-3">
                <label
                  htmlFor="exampleInputEmail1"
                  className="form-label float-start"
                >
                  TopicPassPercentage:
                </label>
                <input
                  type="text"
                  id="examid"
                  className="form-control "
                  placeholder="Topic Pass Percentage * eg(25%)"
                  name="examId"
                  // value={examid}
                  // onChange={(e) => setexamid(e.target.value)}
                />
                <p id="p1" className="text-danger"></p>
              </div>
              <div className="mb-3">
                <label
                  htmlFor="exampleInputEmail1"
                  className="form-label float-start"
                >
                  QuestionsPerExam:
                </label>
                <input
                  type="text"
                  id="examid"
                  className="form-control"
                  placeholder="Questions per Exam * eg(60)"
                  name="examId"
                  // value={examid}
                  // onChange={(e) => setexamid(e.target.value)}
                />
                <p id="p1" className="text-danger"></p>
              </div>
              <div className="mb-3">
                <button className="btn btn-primary" onClick={() => hideForm()}>
                  Submit
                </button>
              </div>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ExamTopicMappingView;
