import React, { useEffect } from "react";
import { useState } from "react";
import "../ExamMaster/ShowExams.css";
import { useNavigate } from "react-router-dom";

const ShowExams = () => {
  const [data, setData] = useState("");
  const [topicData, setTopicData] = useState([]);
  const [Examname, SetexamName] = useState("");
  const [questionData, setQuestionData] = useState([]);
  const [questionName, setQuestionName] = useState("");
 

  const navigate = useNavigate();
  const deleteRows = (index) => {
    const updatedRows = [...data];
    updatedRows.splice(index, 1);
    setData(updatedRows);
    // fetch("https://localhost:8443/onlineexam/control/deleteexam", {
    //   method: "DELETE",
    // });
  };

  const showexamDetails = (examId) => {
    const examIdd = examId;
    localStorage.setItem("examId", examIdd);
    // navigate('addexam');
  };

  async function fetchInfo() {
    console.log("fetch obj is called....");

    const response = await fetch(
      "https://localhost:8443/onlineexam/control/examMasterListEvent"
    );

    const text = await response.json();
    console.log(text);
    setData(text.ExamData);
    const res = text.ExamData;
  }

  useEffect(() => {
    fetchInfo();
  }, []);

  return (
    <div className="container">
      <div className="col-8 ">
        <table className="table table-striped ">
          <thead>
            <tr>
              <th>ExamId</th>
              <th>ExamName</th>

              <th></th>
              <th></th>
              <th></th>
              <th></th>
            </tr>
          </thead>

          <tbody>
            {data
              ? data.map((obj, value) => (
                  <tr>
                    <td>{obj.examId}</td>
                    <td>{obj.examName}</td>
                    <td>{obj.description}</td>
                    {/* <td>{obj.creationDate}</td>
                    <td>{obj.expirationFate}</td>
                    <td>{obj.noOfQuestions}</td>
                    <td>{obj.durationMinutes}</td>
                    <td>{obj.passPercentage}</td> */}
                    {/* <td>{obj.questionsRandomized}</td> */}
                    {/* <td>{obj.answersMust}</td> */}

                    {/* <td>{obj.enableNegativeMark}</td> */}
                    {/* <td>{obj.negativeMarkValue}</td> */}
                    <td>
                      <button
                        className="btn btn-primary"
                        onClick={() => showexamDetails(obj.examId)}
                      >
                        Edit
                      </button>
                    </td>
                    <td>
                      <button
                        className="btn btn-primary"
                        onClick={() => {
                          examDetails(obj.examId);
                        }}
                      >
                        Details
                      </button>
                      {/* <button className="btn btn-primary" onClick={examDetails(obj.examId)}>Details</button> */}
                    </td>

                    <td>
                      <button
                        className="btn btn-danger"
                        onClick={() => deleteRows(value)}
                      >
                        Delete
                      </button>
                    </td>
                  </tr>
                ))
              : ""}
          </tbody>
        </table>
      </div>
      <div className="container my-5">
        <table className="table table-striped">
          <thead>
