import React, { useEffect } from 'react'
import { useNavigate, useParams } from 'react-router-dom';
import { useState } from 'react';

const DetailsOfQuestion = () => {
    const [data,setData]=useState([]);
    const params=useParams();
    const questionId = params.questionId;
    const topicName=params.topicName;
    const navigate=useNavigate();
    console.log("Topic Name....>",topicName);
    console.log("Question Id...>",questionId);
    var topicId;
    async function questionDetails()  {
        console.log("question Details is called....");
        console.log(questionId);
        const map = {
          questionId: questionId,
        };
    
        const response = await fetch(
          "https://localhost:8443/onlineexam/control/questionMasterList",
          {
            method: "POST",
            credentials: "include",
            body: JSON.stringify(map),
            headers: {
              "Content-type": "application/json",
            },
          }
        );
    
        const result = await response.json();
        console.log("result....>",result);
        const myobj = result.getRecord;
        console.log("Myobj....>",myobj);
        setData(result.getRecord);
        console.log("Data..>",data);
        console.log(result.getRecord);
        topicId=result.getRecord.topicId;
      };
      useEffect(() => {
        questionDetails();
      }, []);
  return (
    <div className='my-5'>

        <div className='d-flex shadow'>
            <p className='text-bold my-2 mx-2'>Question Detail - </p>
            <div className='my-2'>{data.questionDetail}</div>
        </div>

        <div className='d-flex shadow'>
            <p className='text-bold my-2 mx-2'>Option A - </p>
            <div className='my-2'>{data.optionA}</div>
        </div>
        <div className='d-flex shadow'>
            <p className='text-bold my-2 mx-2'>Option B - </p>
            <div className='my-2'>{data.optionB}</div>
        </div>
        <div className='d-flex shadow'>
            <p className='text-bold my-2 mx-2'>Option C - </p>
            <div className='my-2'>{data.optionC}</div>
        </div>
        <div className='d-flex shadow'>
            <p className='text-bold my-2 mx-2'>Option D - </p>
            <div className='my-2'>{data.optionD}</div>
        </div>
        <div className='d-flex shadow'>
            <p className='text-bold my-2 mx-2'>Option E - </p>
            <div className='my-2'>{data.optionE}</div>
        </div>
        <div className='d-flex shadow'>
            <p className='text-bold my-2 mx-2'>Answer - </p>
            <div className='my-2'>{data.answer}</div>
        </div>
    {/* <table >
        <thead>
            <tr>
                <th>Option A</th>
                <th>Option B</th>
                <th>Option C</th>
                <th>Option D</th>
                <th>Option E</th>
                <th>Answer</th>
                <th>Question Detail</th>
            </tr>
        </thead>
        <tbody>
          
            <td>
                {data.optionA}
            </td>
            <td>
            {data.optionB}
            </td>
