import React from "react";
import "bootstrap/dist/css/bootstrap.css";
import "../ExamMaster/Login.css";

const Login = () => {

  // const [hasError,setHasError]=[[false]];

  const SubmittingForm=(event)=>{
    console.log("Submitting form called...");
    const data=new FormData(event.target);
    let username=data.get("username");
    let password=data.get("password");
    // hasError[false];
   const myobject=Object.fromEntries(data.entries());
   Object.entries(myobject).map(([key,value],keyIndex)=>{
     validateformlogin(key,value);
     
 });
  }
  function validateformlogin(key,value){
      switch(key){
          case "username":
              if(value=="" || value==null){
                  document.getElementById('p1').classList.remove('d-none');
                  document.getElementById('p1').classList.add('d-block');
                  document.getElementById('p1').innerHTML='Username Should not be Empty';
                  // alert("Username should not be empty");
                  // setHasError[false];
              }
              else{
              let regexx = new RegExp('[a-z0-9]+@[a-z]+\.[a-z]{2,3}');
                
      

              if(!value.test(regexx)){
                  document.getElementById('p1').innerHTML="You have not using the validate name..."
                  // setHasError[false];
              }
              else if(value.test(regexx)){
                    console.log("The entered value is correct...");
                    // setHasError[true];
              }


              }
                break;
          case "password":
            var passw=  /^[A-Za-z]\w{7,14}$/;
            if(value.length<6){

              document.getElementById('p2').innerHTML="Password should be atleast six characters"
            }
            else if(value==""){
              document.getElementById('p2').innerHTML='Password should be not be empty.'
            }
            else{
              if(value.match(passw)){
                    // hasError[true];

              }
            }
            break;
            default:{
              console.log("hii")
            }
     }
  }

  return (
    <div className="row col-lg-12 ">
      <div className="col-3 "></div>
      <div className="col-6 login">
        <form onSubmit={SubmittingForm}>
          <div className="form-group">
            <label for="exampleInputEmail1" className="label1">
              Username
            </label>
            <input
              type="text"
              className="form-control finput"
              id="exampleInputEmail1"
              aria-describedby="emailHelp"
              placeholder="Enter your username"
              name="username"
            />
            <p id="p1"  className="d-none text-dark"></p>
          </div>
          <div className="form-group div2">
            <label for="exampleInputPassword1" className="label1">
              Password
            </label>
            <input
              type="password"
              className="form-control finput"
              id="exampleInputPassword1"
              placeholder="Password"
              name="password"
            />
            <p id="p2" className="d-none text-dark"></p>
          </div>

          <button type="submit"  className="btn btn-primary mt-4">
            Submit
          </button>

          
        </form>
      </div>
      <div className="col-3"></div>
    </div>
  );
};

export default Login;
